import TextField from '../TextField'
import './form.css'
import DropdownList from '../DropdownList'
import Button from '../Button '
import { useState } from 'react'

const Form = () => {
    const Times = [
        'Programação',
        'Front-End',
        'Data Science',
        'Devops',
        'UX e Design',
        'Mobile',
        'Inovação e Gestão'
    ]

    const [name, setname] = useState('')
    const [office, setoffice] = useState('')
    const [image, setimage] = useState('')
    const [time, setTime] = useState('')
    const [phone, setphone] =useState('')

    const ToSave = (event) => {
        event.preventDefault()
        console.log('Form submetido =>', name, office, image, time)
    }
    return (
        <section className="form">
            <form onSubmit={ToSave}>
                <h2>Preencha os dados para criar o card</h2>
                <TextField
                    mandatory={true}
                    label="Nome"
                    placeholder="Digite seu Nome"
                    val={name}
                    toChange={val => setname(val)}
                />
                <TextField
                    mandatory={true}
                    label="Cargo"
                    placeholder="Digite seu Cargo"
                    val={office}
                    toChange={val => setoffice(val)}
                />
                <TextField
                    label="Imagem de Perfil"
                    placeholder="Digite o endereço da Imagem"
                    val={image}
                    toChange={val => setimage(val)}
                />

                <TextField
                    label="Número de telefone (Opcional)"
                    placeholder= "Digite seu telefone com DDD"
                    val={phone}
                    toChange={val => setphone(val)}
                />
                <DropdownList
                    mandatory={true}
                    label='Times'
                    itens={Times}
                    val={time}
                    toChange={val => setTime(val)}
                />
                <Button>
                    Criar Card
                </Button>
            </form>
        </section>
    )
}
export default Form
