import { useState } from 'react';
import Banner from './components/Banner';
import Form from './components/Form';
import Team from './components/Team';
import Footer from './components/Footer';

function App() {

  const Teams = [
    {
      name: 'OpenToWork',
      primarycolor: '#00ca59',
      secundarycolor: '#00ffa1'
    },
    {
      name: 'Hiring',
      primarycolor: '#ae00e5',
      secundarycolor: '#E8F8FF'
    },
    {
      name: 'Data Science',
      primarycolor: '#A6D157',
      secundarycolor: '#F0F8E2'
    },
    {
      name: 'Devops',
      primarycolor: '#E06B69',
      secundarycolor: '#FDE7E8'
    },
    {
      name: 'UX e Design',
      primarycolor: '#D86EBF',
      secundarycolor: '#FAE5F5'
    },
    {
      name: 'Mobile',
      primarycolor: '#FEBA05',
      secundarycolor: '#FFF5D9'
    },
    {
      name: 'Inovação e Gestão',
      primarycolor: '#FF8A29',
      secundarycolor: '#FFEEDF'
    },
  ]

  const [users, setUsers] = useState([])
  const toNewUserRegistered = (user) => {
    setUsers([...users, user])
  }

  return (
    <div className="App">
      <Banner />
      <Form teams={Teams.map(Team => Team.name)} toUserRegistered={user => toNewUserRegistered(user)} />

      {Teams.map(team =>
        <Team
          key={team.name}
          name={team.name}
          primarycolor={team.primarycolor}
          secundarycolor={team.secundarycolor}
          Users={users.filter(user => user.team === team.name)}
        />)}
      <Footer/>
    </div>
  );
}

export default App;
